import React, { useState } from 'react';
import RecipeList from './components/RecipeList';
import AddRecipeForm from './components/AddRecipeForm';
import SearchBar from './components/SearchBar';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const addRecipe = (newRecipe) => {
    setRecipes([...recipes, newRecipe]);
  };

  return (
    <div className="bg-gray-900 min-h-screen p-10">
      <h1 className="text-4xl font-bold text-yellow-400 text-center mb-8 animate__animated animate__fadeIn">Culinary Delights</h1>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      <AddRecipeForm addRecipe={addRecipe} />
      <RecipeList recipes={recipes.filter(recipe => recipe.title.toLowerCase().includes(searchTerm.toLowerCase()))} />
    </div>
  );
}

export default App;
