import React, { useState } from 'react';
import RecipeList from './components/RecipeList';
import SearchBar from './components/SearchBar';
import AddRecipeForm from './components/AddRecipeForm';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([
    {
      id: 1,
      name: 'Pasta',
      ingredients: ['Pasta', 'Tomato Sauce', 'Cheese'],
      instructions: 'Boil pasta for 10 minutes, drain, and add sauce.',
      image: 'https://example.com/pasta.jpg',
    },
    {
      id: 2,
      name: 'Pizza',
      ingredients: ['Dough', 'Tomato Sauce', 'Cheese', 'Toppings'],
      instructions: 'Bake dough, add sauce, cheese, and toppings.',
      image: 'https://example.com/pizza.jpg',
    },
    {
      id: 3,
      name: 'Salad',
      ingredients: ['Lettuce', 'Tomato', 'Cucumber', 'Dressing'],
      instructions: 'Chop vegetables and mix with dressing.',
      image: 'https://example.com/salad.jpg',
    },
    {
      id: 4,
      name: 'Chocolate Cake',
      ingredients: ['Flour', 'Sugar', 'Cocoa Powder', 'Eggs'],
      instructions: 'Mix ingredients and bake for 30 minutes.',
      image: 'https://example.com/cake.jpg',
    },
    {
      id: 5,
      name: 'Sushi',
      ingredients: ['Rice', 'Seaweed', 'Fish', 'Vegetables'],
      instructions: 'Roll ingredients in seaweed and slice.',
      image: 'https://example.com/sushi.jpg',
    },
    {
      id: 6,
      name: 'Tacos',
      ingredients: ['Tortillas', 'Meat', 'Cheese', 'Lettuce', 'Salsa'],
      instructions: 'Fill tortillas with ingredients and serve.',
      image: 'https://example.com/tacos.jpg',
    },
    {
      id: 7,
      name: 'Burger',
      ingredients: ['Bun', 'Patty', 'Lettuce', 'Tomato', 'Cheese'],
      instructions: 'Cook patty and assemble the burger.',
      image: 'https://example.com/burger.jpg',
    },
    {
      id: 8,
      name: 'Pancakes',
      ingredients: ['Flour', 'Milk', 'Eggs', 'Baking Powder'],
      instructions: 'Mix ingredients and cook on a skillet.',
      image: 'https://example.com/pancakes.jpg',
    },
    {
      id: 9,
      name: 'Fried Rice',
      ingredients: ['Rice', 'Eggs', 'Vegetables', 'Soy Sauce'],
      instructions: 'Stir-fry all ingredients together.',
      image: 'https://example.com/friedrice.jpg',
    },
    {
      id: 10,
      name: 'Omelette',
      ingredients: ['Eggs', 'Cheese', 'Vegetables'],
      instructions: 'Whisk eggs, pour into pan, and add fillings.',
      image: 'https://example.com/omelette.jpg',
    },
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const filteredRecipes = recipes.filter(recipe =>
    recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addRecipe = (newRecipe) => {
    setRecipes([...recipes, newRecipe]);
  };

  return (
    <div className="app">
      <h1>Recipe App</h1>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      <AddRecipeForm addRecipe={addRecipe} />
      <RecipeList recipes={filteredRecipes} />
    </div>
  );
}

export default App;
