import React, { useState } from 'react';
import '';

const AddRecipeForm = ({ setRecipes }) => {
  const [newRecipe, setNewRecipe] = useState({ title: '', ingredients: '', instructions: '', image: '' });

  const handleChange = (e) => {
    setNewRecipe({ ...newRecipe, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const ingredientsArray = newRecipe.ingredients.split(',').map(item => item.trim());
    setRecipes(prevRecipes => [...prevRecipes, { ...newRecipe, ingredients: ingredientsArray }]);
    setNewRecipe({ title: '', ingredients: '', instructions: '', image: '' });
  };

  return (
    <form className="add-recipe-form" onSubmit={handleSubmit}>
      <h2>Add a New Recipe</h2>
      <input type="text" name="title" placeholder="Recipe Title" value={newRecipe.title} onChange={handleChange} required />
      <input type="text" name="ingredients" placeholder="Ingredients (comma separated)" value={newRecipe.ingredients} onChange={handleChange} required />
      <textarea name="instructions" placeholder="Instructions" value={newRecipe.instructions} onChange={handleChange} required />
      <input type="text" name="image" placeholder="Image URL" value={newRecipe.image} onChange={handleChange} required />
      <button type="submit">Add Recipe</button>
    </form>
  );
};

export default AddRecipeForm;
