import React, { useState } from 'react';
import './styles/App.css';
import RecipeList from './components/RecipeList';
import AddRecipeForm from './components/AddRecipeForm';
import SearchBar from './components/SearchBar';

const initialRecipes = [
  {
    title: 'Dhokla',
    ingredients: ['Chickpea flour', 'Yogurt', 'Ginger', 'Green chili', 'Mustard seeds', 'Curry leaves'],
    instructions: 'Mix the ingredients, steam for 15 minutes, and temper with mustard seeds and curry leaves.',
    image: 'https://www.vegrecipesofindia.com/wp-content/uploads/2019/09/dhokla-recipe-1.jpg',
  },
  {
    title: 'Khandvi',
    ingredients: ['Gram flour', 'Yogurt', 'Ginger paste', 'Turmeric', 'Mustard seeds', 'Coconut'],
    instructions: 'Cook the batter until it thickens, spread, roll, and garnish.',
    image: 'https://www.cookwithmanali.com/wp-content/uploads/2018/06/Khandvi-Recipe.jpg',
  },
  {
    title: 'Handvo',
    ingredients: ['Rice flour', 'Lentils', 'Curd', 'Ginger', 'Green chilies', 'Sesame seeds'],
    instructions: 'Mix, ferment, and bake until golden.',
    image: 'https://www.cookclickndevour.com/wp-content/uploads/2020/07/handvo-recipe-1.jpg',
  },
  {
    title: 'Thepla',
    ingredients: ['Whole wheat flour', 'Fenugreek leaves', 'Yogurt', 'Spices'],
    instructions: 'Knead, roll, and cook on a skillet.',
    image: 'https://www.indianhealthyrecipes.com/wp-content/uploads/2021/06/thepla-recipe.jpg',
  },
  {
    title: 'Patra',
    ingredients: ['Colocasia leaves', 'Gram flour', 'Spices', 'Jaggery'],
    instructions: 'Spread the batter on leaves, roll, steam, and slice.',
    image: 'https://www.cookwithmanali.com/wp-content/uploads/2018/06/Patra-Recipe.jpg',
  },
  {
    title: 'Undhiyu',
    ingredients: ['Mixed vegetables', 'Spices', 'Green beans', 'Eggplant'],
    instructions: 'Cook together with spices until soft.',
    image: 'https://www.vegrecipesofindia.com/wp-content/uploads/2019/08/undhiyu-recipe.jpg',
  },
  {
    title: 'Sev Tameta',
    ingredients: ['Tomatoes', 'Sev', 'Spices', 'Onion'],
    instructions: 'Cook tomatoes with spices, top with sev before serving.',
    image: 'https://www.cookwithmanali.com/wp-content/uploads/2017/08/Sev-Tameta-Recipe.jpg',
  },
  {
    title: 'Dal Dhokli',
    ingredients: ['Whole wheat flour', 'Lentils', 'Spices', 'Vegetables'],
    instructions: 'Cook lentils and add the dough pieces, simmer until cooked.',
    image: 'https://www.indianhealthyrecipes.com/wp-content/uploads/2021/07/dal-dhokli-recipe.jpg',
  },
  {
    title: 'Aloo Paratha',
    ingredients: ['Whole wheat flour', 'Potatoes', 'Spices'],
    instructions: 'Stuff the paratha and cook on a skillet.',
    image: 'https://www.vegrecipesofindia.com/wp-content/uploads/2020/10/aloo-paratha-recipe-1.jpg',
  },
  {
    title: 'Chaas',
    ingredients: ['Yogurt', 'Water', 'Spices', 'Mint leaves'],
    instructions: 'Blend yogurt and water, add spices, and serve chilled.',
    image: 'https://www.indianhealthyrecipes.com/wp-content/uploads/2021/05/chaas-recipe.jpg',
  },
];

function App() {
  const [recipes, setRecipes] = useState(initialRecipes);
  const [searchTerm, setSearchTerm] = useState('');

  const filteredRecipes = recipes.filter(recipe =>
    recipe.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="app-container">
      <h1 className="app-title">Gujarati Aap</h1>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      <AddRecipeForm setRecipes={setRecipes} />
      <RecipeList recipes={filteredRecipes} />
    </div>
  );
}

export default App;
