import React, { useState } from 'react';
import RecipeList from './components/RecipeList';
import AddRecipeForm from './components/AddRecipeForm';
import SearchBar from './components/SearchBar';

const initialRecipes = [
  {
    title: 'Spaghetti Carbonara',
    ingredients: ['Spaghetti', 'Eggs', 'Parmesan cheese', 'Pancetta', 'Pepper'],
    instructions: 'Cook the spaghetti. In a bowl, mix eggs and cheese. Fry pancetta, add spaghetti, and mix with egg mixture.',
    image: 'https://example.com/spaghetti-carbonara.jpg',
  },
  {
    title: 'Chicken Tikka Masala',
    ingredients: ['Chicken', 'Yogurt', 'Tomato sauce', 'Spices', 'Rice'],
    instructions: 'Marinate chicken, cook with spices, add tomato sauce, and serve with rice.',
    image: 'https://example.com/chicken-tikka-masala.jpg',
  },
  {
    title: 'Caesar Salad',
    ingredients: ['Lettuce', 'Croutons', 'Parmesan cheese', 'Caesar dressing'],
    instructions: 'Combine lettuce, croutons, and dressing. Top with Parmesan cheese.',
    image: 'https://example.com/caesar-salad.jpg',
  },
  {
    title: 'Beef Tacos',
    ingredients: ['Ground beef', 'Taco shells', 'Lettuce', 'Tomatoes', 'Cheese'],
    instructions: 'Cook beef, fill taco shells, and top with lettuce, tomatoes, and cheese.',
    image: 'https://example.com/beef-tacos.jpg',
  },
];

function App() {
  const [recipes, setRecipes] = useState(initialRecipes);
  const [searchTerm, setSearchTerm] = useState('');

  const addRecipe = (newRecipe) => {
    setRecipes([...recipes, newRecipe]);
  };

  return (
    <div className="min-h-screen p-10">
      <h1 className="text-4xl font-bold text-yellow-400 text-center mb-8 animate__animated animate__fadeIn">Culinary Delights</h1>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      <AddRecipeForm addRecipe={addRecipe} />
      <RecipeList recipes={recipes.filter(recipe => recipe.title.toLowerCase().includes(searchTerm.toLowerCase()))} />
    </div>
  );
}

export default App;
