import React, { useState } from 'react';
import RecipeList from './RecipeList';
import SearchBar from './SearchBar';
import AddRecipeForm from './AddRecipeForm';
import './App.css';

function App() {
  const [recipes, setRecipes] = useState([
    {
      id: 1,
      name: 'Spaghetti Bolognese',
      ingredients: ['Spaghetti', 'Tomato Sauce', 'Ground Beef'],
      instructions: 'Cook spaghetti and mix with sauce and ground beef.',
      time: '30 minutes',
      servings: 4
    },
    {
      id: 2,
      name: 'Chicken Curry',
      ingredients: ['Chicken', 'Curry Paste', 'Coconut Milk'],
      instructions: 'Cook chicken, add curry paste, and simmer with coconut milk.',
      time: '45 minutes',
      servings: 3
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const addRecipe = (newRecipe) => {
    const updatedRecipes = [...recipes, { ...newRecipe, id: recipes.length + 1 }];
    setRecipes(updatedRecipes);
  };

  const filteredRecipes = recipes.filter(recipe =>
    recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <h1>Recipe App</h1>
      <SearchBar onSearch={handleSearch} />
      <AddRecipeForm addRecipe={addRecipe} />
      <RecipeList recipes={filteredRecipes} />
    </div>
  );
}

export default App;
