import React from 'react';

const RecipeCard = ({ recipe }) => {
  return (
    <div className="bg-gray-800 rounded-lg shadow-lg hover:shadow-2xl transition duration-300 ease-in-out transform hover:-translate-y-1 p-6 mb-6 animate-fade-in">
      <img src={recipe.image} alt={recipe.title} className="w-full h-48 object-cover rounded-t-lg mb-4" />
      <h3 className="text-xl font-bold text-yellow-400 mb-2">{recipe.title}</h3>
      <ul className="mt-2 text-gray-300">
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index} className="list-disc ml-5">{ingredient}</li>
        ))}
      </ul>
      <p className="mt-2 text-gray-400">{recipe.instructions}</p>
    </div>
  );
};

export default RecipeCard;
